contracts/Forwarder.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract Forwarder is ReentrancyGuard {
    using ECDSA for bytes32;

    address public owner;

    mapping(address => uint256) public nonces;

    event Forwarded(address indexed from, address indexed to, uint256 value, uint256 nonce);

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not authorized");
        _;
    }

    function forwardERC20(
        address token,
        address to,
        uint256 amount,
        uint256 nonce,
        bytes memory signature
    ) external nonReentrant {
        require(nonce == nonces[msg.sender]++, "Invalid nonce");
        bytes32 messageHash = keccak256(abi.encodePacked(msg.sender, token, to, amount, nonce));
        require(_recoverSigner(messageHash, signature) == msg.sender, "Invalid signature");

        IERC20(token).transferFrom(msg.sender, to, amount);
        emit Forwarded(msg.sender, to, amount, nonce);
    }

    function forwardERC721(
        address token,
        address to,
        uint256 tokenId,
        uint256 nonce,
        bytes memory signature
    ) external nonReentrant {
        require(nonce == nonces[msg.sender]++, "Invalid nonce");
        bytes32 messageHash = keccak256(abi.encodePacked(msg.sender, token, to, tokenId, nonce));
        require(_recoverSigner(messageHash, signature) == msg.sender, "Invalid signature");

        IERC721(token).safeTransferFrom(msg.sender, to, tokenId);
        emit Forwarded(msg.sender, to, tokenId, nonce);
    }

    //function _recoverSigner(bytes32 hash, bytes memory signature) internal pure returns (address) {
      //  return hash.toEthSignedMessageHash().recover(signature);
    //}
    function _recoverSigner(bytes32 hash, bytes memory signature) internal pure returns (address) {
        // Implementing Ethereum Signed Message prefix
        bytes32 ethSignedMessageHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)
        );
        return ECDSA.recover(ethSignedMessageHash, signature);
    }


    // Fallback function to receive ETH for gas
    receive() external payable {}

    // Owner can withdraw remaining ETH
    function withdrawETH() external onlyOwner {
        payable(owner).transfer(address(this).balance);
    }
}

contracts/Rel.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Rel {
    event ERC20Relayed(address indexed user, address indexed token, address indexed recipient, uint256 amount, uint256 nonce);
    event ERC721Relayed(address indexed user, address indexed token, address indexed recipient, uint256 tokenId, uint256 nonce);

    mapping(address => uint256) public nonces;

    function relayERC20(
        address user,
        address token,
        address recipient,
        uint256 amount,
        uint256 nonce,
        bytes memory signature
    ) external {
        require(nonces[user] == nonce, "Invalid nonce");

        // Recreate the hash
        bytes32 messageHash = keccak256(abi.encodePacked(user, token, recipient, amount, nonce));

        // Recover the signer from the signature
        bytes32 ethSignedMessageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash));
        address signer = recoverSigner(ethSignedMessageHash, signature);

        require(signer == user, "Invalid signature");

        // Update the nonce for the user
        nonces[user]++;

        // Transfer the ERC20 tokens (requires approval)
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0xa9059cbb, recipient, amount)
        ); // Transfer function selector
        require(success && (data.length == 0 || abi.decode(data, (bool))), "Token transfer failed");

        emit ERC20Relayed(user, token, recipient, amount, nonce);
    }

    function relayERC721(
        address user,
        address token,
        address recipient,
        uint256 tokenId,
        uint256 nonce,
        bytes memory signature
    ) external {
        require(nonces[user] == nonce, "Invalid nonce");

        // Recreate the hash
        bytes32 messageHash = keccak256(abi.encodePacked(user, token, recipient, tokenId, nonce));

        // Recover the signer from the signature
        bytes32 ethSignedMessageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash));
        address signer = recoverSigner(ethSignedMessageHash, signature);

        require(signer == user, "Invalid signature");

        // Update the nonce for the user
        nonces[user]++;

        // Transfer the ERC721 token (requires approval)
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x23b872dd, user, recipient, tokenId)
        ); // TransferFrom function selector
        require(success && (data.length == 0 || abi.decode(data, (bool))), "Token transfer failed");

        emit ERC721Relayed(user, token, recipient, tokenId, nonce);
    }

    function recoverSigner(bytes32 hash, bytes memory signature) public pure returns (address) {
        bytes32 r;
        bytes32 s;
        uint8 v;
        // Check the signature length
        if (signature.length != 65) {
            return address(0);
        }
        // Divide the signature in r, s and v variables
        assembly {
            r := mload(add(signature, 32))
            s := mload(add(signature, 64))
            v := byte(0, mload(add(signature, 96)))
        }
        // Version of signature should be 27 or 28, but 0 and 1 are also possible
        if (v < 27) {
            v += 27;
        }
        // If the version is correct return the signer address
        if (v != 27 && v != 28) {
            return address(0);
        } else {
            return ecrecover(hash, v, r, s);
        }
    }
}

contracts/TestNFT.sol.content:
// contracts/TestNFT.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

contract TestNFT is ERC721 {
    uint256 private _tokenIdCounter;

    constructor() ERC721("TestNFT", "TNFT") {}

    function _baseURI() internal view virtual override returns (string memory) {
        return "https://myapi.com/metadata/";
    }

    function safeMint(address to) public {
        uint256 tokenId = _tokenIdCounter;
        _safeMint(to, tokenId);
        _tokenIdCounter += 1;
    }
}

